</node>
<node CREATED="1287469710216" FOLDED="true" ID="ID_622278508" MODIFIED="1287469719225" TEXT="Notes">
<font NAME="Monospaced" SIZE="12"/>
<node CREATED="1270986284266" FOLDED="true" ID="ID_620176256" MODIFIED="1270986287056" TEXT="Importing">
<node CREATED="1278168818829" FOLDED="true" ID="ID_360509088" MODIFIED="1279334181333" TEXT="import ...">
<node CREATED="1270986295262" FOLDED="true" ID="ID_1048975" MODIFIED="1278169008009" TEXT="import module [as alias][, module [as alias]]">
<node CREATED="1269479856737" ID="ID_39856374" MODIFIED="1270986905978" TEXT="&gt;&gt;&gt; import math&#xa;&gt;&gt;&gt; math.sqrt(9)&#xa;3.0&#xa;&gt;&gt;&gt; import math as mth&#xa;&gt;&gt;&gt; mth.sqrt(9)&#xa;3.0&#xa;"/>
</node>
<node CREATED="1278168164529" FOLDED="true" ID="ID_155489783" MODIFIED="1278197218234" TEXT="&#x5f15;&#x5165;&#x67d0;&#x500b; Module, &#x9664;&#x975e;&#x7d66;&#x5b9a; Alias, &#x5426;&#x5247;&#x9084;&#x662f;&#x5f97;&#x7528;&#x5168;&#x540d;&#x4f86;&#x5b58;&#x53d6;; &#x8ddf; Java &#x7684; import &#x5f88;&#x4e0d;&#x4e00;&#x6a23;">
<node CREATED="1278167846868" ID="ID_857475260" MODIFIED="1279334087164" TEXT="&gt;&gt;&gt; import xml.dom # &#x4e0d;&#x50cf; Java &#x7684; import, &#x4e4b;&#x5f8c;&#x9084;&#x662f;&#x5f97;&#x7528;&#x5168;&#x540d;&#x4f86;&#x5b58;&#x53d6;&#xa;&gt;&gt;&gt; dom.EMPTY_NAMESPACE&#xa;Traceback (most recent call last):&#xa;  File &quot;&lt;stdin&gt;&quot;, line 1, in &lt;module&gt;&#xa;NameError: name &apos;dom&apos; is not defined&#xa;&gt;&gt;&gt; xml.dom.EMPTY_NAMESPACE # &#x7528;&#x5168;&#x540d;&#x53d6;&#x5b58; OK (&#x6c92;&#x6709;&#x4efb;&#x4f55;&#x8f38;&#x51fa;, &#x8868;&#x793a;&#x5167;&#x542b; None)&#xa;&gt;&gt;&gt; import xml.dom as dom&#xa;&gt;&gt;&gt; dom.EMPTY_NAMESPACE # &#x7d66;&#x5b9a; Alias &#x5f8c;, &#x53ef;&#x4ee5;&#x900f;&#x904e;&#x8f03;&#x7c21;&#x77ed;&#x7684; Alias &#x5b58;&#x53d6;&#xa;&gt;&gt;&gt; &#xa;"/>
</node>
</node>
<node CREATED="1278168822829" ID="ID_1844454445" MODIFIED="1279334187114" TEXT="from ... import ...">
<node CREATED="1270986315619" FOLDED="true" ID="ID_1071054909" MODIFIED="1278169015276" TEXT="from module import something [as alias][, something [as alias]]">
<node CREATED="1269479870228" ID="ID_1665020812" MODIFIED="1270986897500" TEXT="&gt;&gt;&gt; from math import sqrt # &#x7b49;&#x540c;&#x65bc; import math &#x4e4b;&#x5f8c;, sqrt = math.sqrt &#x7684;&#x5beb;&#x6cd5;&#xa;&gt;&gt;&gt; sqrt(9)&#xa;3.0&#xa;&gt;&gt;&gt; from math import sqrt as sqt&#xa;&gt;&gt;&gt; sqt(9)&#xa;3.0&#xa;"/>
<node CREATED="1273480309883" ID="ID_713152232" MODIFIED="1278168770074" TEXT="from datetime import datetime&#xa;from datetime import date, time # datetime Module &#x4e26;&#x6c92;&#x6709;&#x88ab; Alias &#x7d66; Hide &#x6389;"/>
</node>
<node CREATED="1279924135975" ID="ID_2911678" MODIFIED="1279924204465" TEXT="from package import module">
