<node CREATED="1299039013516" ID="ID_899020432" MODIFIED="1299039066177" TEXT="/tmp$ cat file.txt&#xa;line1&#xa;line2&#xa;line3 &lt;-- &#x76f4;&#x63a5;&#x7d50;&#x675f;&#xa;/tmp$ cat file_emptyend.txt&#xa;line1&#xa;line2&#xa;line3&#xa;      &lt;-- &#x7d50;&#x675f;&#x5728;&#x55ae;&#x7368;&#x7684;&#x4e00;&#x884c;&#xa;/tmp$ python&#xa;&gt;&gt;&gt; open(&apos;file.txt&apos;).readlines()&#xa;[&apos;line1\n&apos;, &apos;line2\n&apos;, &apos;line3\n&apos;] &lt;-- &#x70ba;&#x4ec0;&#x9ebc;&#x9019;&#x88e1;&#x6709; newline?&#xa;&gt;&gt;&gt; open(&apos;file_emptyend.txt&apos;).readlines()&#xa;[&apos;line1\n&apos;, &apos;line2\n&apos;, &apos;line3\n&apos;, &apos;\n&apos;]&#xa;&gt;&gt;&gt;&#xa;&gt;&gt;&gt;&#xa;&gt;&gt;&gt; for line in open(&apos;file.txt&apos;): print repr(line),&#xa;... &#xa;&apos;line1\n&apos; &apos;line2\n&apos; &apos;line3\n&apos;&#xa;&gt;&gt;&gt; for line in open(&apos;file_emptyend.txt&apos;): print repr(line),&#xa;... &#xa;&apos;line1\n&apos; &apos;line2\n&apos; &apos;line3\n&apos; &apos;\n&apos;&#xa;"/>
</node>
<node CREATED="1299039738576" FOLDED="true" ID="ID_564212929" MODIFIED="1299039739874" TEXT="Windows">
<node CREATED="1299039753105" ID="ID_614675745" MODIFIED="1299039852246" TEXT="C:\tmp&gt;type file.txt&#xa;line1&#xa;line2&#xa;line3&#xa;C:\tmp&gt;type file_emptyend.txt&#xa;line1&#xa;line2&#xa;line3&#xa;&#xa;C:\tmp&gt;python&#xa;&gt;&gt;&gt; open(r&apos;C:\tmp\file.txt&apos;).readlines()&#xa;[&apos;line1\n&apos;, &apos;line2\n&apos;, &apos;line3&apos;]&#xa;&gt;&gt;&gt; open(r&apos;C:\tmp\file_emptyend.txt&apos;).readlines()&#xa;[&apos;line1\n&apos;, &apos;line2\n&apos;, &apos;line3\n&apos;] &lt;-- &#x6700;&#x5f8c;&#x4e00;&#x884c;&#x4e0d;&#x898b;&#x4e86;&#xa;&gt;&gt;&gt;"/>
