<node CREATED="1280801629045" FOLDED="true" ID="ID_149797207" LINK="http://docs.python.org/library/os.path.html" MODIFIED="1288333714111" TEXT="os.path (STD)">
<node CREATED="1280801733468" FOLDED="true" ID="ID_576503402" MODIFIED="1280801734898" TEXT="Overview">
<node CREATED="1284204970540" ID="ID_881404058" MODIFIED="1288316133517" TEXT="This module implements some useful functions on pathnames. To access the filesystem see the `os` module"/>
<node CREATED="1284205099241" ID="ID_700736913" MODIFIED="1288316153266" TEXT="On Windows, many of these functions do not properly support UNC pathnames. `splitunc()` and `ismount()` do handle them correctly."/>
<node CREATED="1284205144053" FOLDED="true" ID="ID_157264324" MODIFIED="1284205986456" TEXT="Since different operating systems have different path name conventions, there are several versions of this module in the standard library. (&#x5b83;&#x5011;&#x90fd;&#x5177;&#x6709;&#x76f8;&#x540c;&#x7684; Interface)">
<node CREATED="1284205166504" ID="ID_117356131" MODIFIED="1288316196643" TEXT="The `os.path` module is always the path module suitable for the operating system Python is running on, and therefore usable for local paths."/>
<node CREATED="1284205173527" FOLDED="true" ID="ID_772797689" MODIFIED="1284205982488" TEXT="However, you can also import and use the individual modules if you want to manipulate a path that is always in one of the different formats.">
<node CREATED="1284205204740" ID="ID_433447986" MODIFIED="1288316216871" TEXT="`posixpath` for UNIX-style paths"/>
<node CREATED="1284205204742" ID="ID_1013462603" MODIFIED="1288316218954" TEXT="`ntpath` for Windows paths"/>
<node CREATED="1284205204743" ID="ID_18837327" MODIFIED="1288316221275" TEXT="`macpath` for old-style MacOS paths"/>
<node CREATED="1284205204746" ID="ID_1140588573" MODIFIED="1288316223197" TEXT="`os2emxpath` for OS/2 EMX paths"/>
</node>
</node>
</node>
<node CREATED="1284204837711" ID="ID_740837062" MODIFIED="1284204838949" TEXT="API">
<node CREATED="1284208889335" ID="ID_921157104" MODIFIED="1288316264121" TEXT="abspath(path) [1.5.2+]">
<font BOLD="true" NAME="Monospaced" SIZE="12"/>
<node CREATED="1288316244522" ID="ID_64033325" MODIFIED="1288316246085" TEXT="Overview">
<node CREATED="1284209183284" ID="ID_335692958" MODIFIED="1300155219790" TEXT="Return a normalized absolutized version of the pathname `path`. (relative path &#x7e3d;&#x662f;&#x88ab;&#x8996;&#x70ba;&#x76f8;&#x5c0d;&#x65bc; current working directory, &#x53c3;&#x8003; `os.getcwd()` &#x7684;&#x8aaa;&#x660e;)"/>
<node CREATED="1284209205401" ID="ID_529266249" MODIFIED="1288316494989" TEXT="On most platforms, this is equivalent to `normpath(join(os.getcwd(), path))`"/>
