<node CREATED="1296536563389" FOLDED="true" ID="ID_1749777940" MODIFIED="1296536568094" TEXT="Date/Time Formats">
<node CREATED="1296536666066" ID="ID_135785122" MODIFIED="1296536735366" TEXT="Remember The Milk supports a number of date and time formats to save you time when entering due dates."/>
<node CREATED="1296536739054" FOLDED="true" ID="ID_1271367299" MODIFIED="1296536742398" TEXT="Following are examples of the ways that you can input dates and times.">
<node CREATED="1296537512348" ID="ID_107089719" MODIFIED="1296537512348" TEXT="`Today` - Today (`tod` also works)"/>
<node CREATED="1296537512350" ID="ID_487323149" MODIFIED="1296537512350" TEXT="`Tomorrow` - Tomorrow (`tom` also works)"/>
<node CREATED="1296537512350" ID="ID_887526038" MODIFIED="1296537512350" TEXT="`25 Apr` - April 25 this year (unless April 25 has passed, in which case it assumes next year)"/>
<node CREATED="1296537512356" ID="ID_465745059" MODIFIED="1296537512356" TEXT="`Apr 25` - April 25 this year (unless April 25 has passed, in which case it assumes next year)"/>
<node CREATED="1296537512359" ID="ID_888643293" MODIFIED="1296537512359" TEXT="`04/25/2008` - April 25, 2008"/>
<node CREATED="1296537512360" ID="ID_678410244" MODIFIED="1296537512360" TEXT="`25/04/2008` - April 25, 2008"/>
<node CREATED="1296537512360" ID="ID_1451331156" MODIFIED="1296537512360" TEXT="`2008/04/25` - April 25, 2008"/>
<node CREATED="1296537512361" ID="ID_1413654890" MODIFIED="1296537512361" TEXT="`2008-04-25` - April 25, 2008"/>
<node CREATED="1296537512362" ID="ID_838671199" MODIFIED="1296537512362" TEXT="`25th` - 25th day of the current month"/>
<node CREATED="1296537512362" ID="ID_1306110762" MODIFIED="1296537512362" TEXT="`End of month` - Last day of the current month"/>
<node CREATED="1296537512363" ID="ID_675094669" MODIFIED="1296537512363" TEXT="`Friday` - The next Friday to occur"/>
<node CREATED="1296537512363" ID="ID_1945706702" MODIFIED="1296537512363" TEXT="`Next Friday` - The second Friday to occur"/>
<node CREATED="1296537512364" ID="ID_262010443" MODIFIED="1296537512364" TEXT="`Fri at 7pm` - Friday at 7:00pm"/>
<node CREATED="1296537512364" ID="ID_428093063" MODIFIED="1296537512364" TEXT="`Fri @ 7pm` - Friday at 7:00pm"/>
<node CREATED="1296537512365" ID="ID_376058026" MODIFIED="1296537512365" TEXT="`6pm` - Today at 6:00pm (unless 6:00pm has passed, in which case it assumes tomorrow)"/>
<node CREATED="1296537512369" ID="ID_1352213887" MODIFIED="1296537512369" TEXT="`18:00`Today at 6:00pm (unless 6:00pm has passed, in which case it assumes tomorrow)"/>
<node CREATED="1296537512370" ID="ID_965995510" MODIFIED="1298277899613" TEXT="`5 hours` - 5 hours from now&#xff08;&#x53ef;&#x60dc;&#x4e0d;&#x652f;&#x63f4; `5h` &#x66f4;&#x7c21;&#x6f54;&#x7684;&#x8868;&#x793a;&#x6cd5;&#xff09;">
<font NAME="Monospaced" SIZE="12"/>
</node>
