<node CREATED="1273462496530" ID="ID_924619017" LINK="http://docs.python.org/library/time.html" MODIFIED="1288748623390" TEXT="time (STD)">
<node CREATED="1288748773362" ID="ID_475405760" MODIFIED="1288748775005" TEXT="Overview">
<node CREATED="1288748775484" ID="ID_549340766" MODIFIED="1288748775484" TEXT="This module provides various time-related functions. For related functionality, see also the `datetime` and `calendar` modules."/>
<node CREATED="1288748815425" FOLDED="true" ID="ID_297318820" MODIFIED="1293183738945" TEXT="Although this module is always available, not all functions are available on all platforms.">
<font BOLD="true" NAME="Monospaced" SIZE="12"/>
<icon BUILTIN="messagebox_warning"/>
<node CREATED="1288748828528" ID="ID_1670550204" MODIFIED="1293183759090" TEXT="Most of the functions defined in this module call platform C library functions with the same name."/>
<node CREATED="1293183759482" ID="ID_907477586" MODIFIED="1293183759788" TEXT="It may sometimes be helpful to consult the platform documentation, because the semantics of these functions varies among platforms."/>
</node>
</node>
<node CREATED="1288748963432" ID="ID_1985417510" MODIFIED="1288748964666" TEXT="API">
<node CREATED="1293184085270" FOLDED="true" ID="ID_1632785063" MODIFIED="1293184085270" TEXT="ctime([secs])">
<node CREATED="1293184104529" ID="ID_1761529876" MODIFIED="1293184105854" TEXT="Overview">
<node CREATED="1293184110783" ID="ID_27041914" MODIFIED="1293184397874" TEXT="Convert a time expressed in seconds since the epoch to a &apos;&apos;string representing&apos;&apos; local time. Locale information is not used by `ctime()`. (&#x8ddf; atime, ctime, mtime &#x7684;&#x6982;&#x5ff5;&#x7121;&#x95dc;!!)"/>
<node CREATED="1293184129271" FOLDED="true" ID="ID_1118611570" MODIFIED="1293184321051" TEXT="If `secs` is not provided or `None`, the current time as returned by `time()` is used. `ctime(secs)` is equivalent to `asctime(localtime(secs))`.">
<node CREATED="1293184139667" ID="ID_856143" MODIFIED="1293184139667" TEXT="Changed in version 2.1: Allowed `secs` to be omitted."/>
<node CREATED="1293184139668" ID="ID_744809959" MODIFIED="1293184139668" TEXT="Changed in version 2.4: If `secs` is `None`, the current time is used."/>
</node>
</node>
